CREATE PROCEDURE [dom.udi].[Name]	(@ID int = NULL, @FirstName varchar(255) = NULL, @LastName varchar(255) = NULL) AS SET NOCOUNT ONIF @ID IS NULL BEGIN	INSERT [dbo].[Name] ([FirstName], [LastName]) VALUES (@FirstName, @LastName)	SELECT SCOPE_IDENTITY() ENDELSE BEGIN	IF COALESCE(NULL, CONVERT(VARCHAR, @FirstName), CONVERT(VARCHAR, @LastName)) IS NOT NULL		UPDATE [dbo].[Name] SET [FirstName]=@FirstName, [LastName]=@LastName WHERE ID = @ID	ELSE DELETE [dbo].[Name] WHERE ID = @ID	SELECT @@ROWCOUNTEND